import React from "react";
import "../app.css";
import axios from 'axios';

function Registration() {

    const [fname, setFname] = useState("");
    const [lname, setLname] = useState("");
    const [email, setEmail] = useState("");
    const [pass, setPass] = useState("");
    const [msg, setMessage] = useState("");

    const onChangeFname = (e) => setFname(e.target.value);
    const onChangeLname = (e) => setLname(e.target.value);
    const onChangeEmail = (e) => setEmail(e.target.value);
    const onChangePass = (e) => setPass(e.target.value);

    const handleSubmit = (evt) => {
        evt.preventDefault();
        console.log(`Form submitted:`);
        console.log(`NAME: ${fname}`);
        console.log(`EMAIL: ${email}`);

        const userinfo = {
            fname: fname,
            lname: lname,
            email: email,
            password: pass,
        }

        axios.post('http://localhost:4000/register', userinfo)
            .then(res => {
                console.log(res.data)
                setMessage('REGISTRATION SUCCESSFUL')
            });

        setFname('')
        setEmail('')
        setPass('')

    }

    return (
        <div>
            <NavigationBar />
            <br />
            <h3>REGISTRATION FORM</h3>
            <h4 style={{ color: "brown" }}> {msg}</h4>
            <form onSubmit={handleSubmit}>
                <input type="text" value={fname}
                    onChange={onChangeFname} placeholder="Enter First Name"
                    required />
                <br /><br />

                <input type="text" value={lname}
                    onChange={onChangeLname} placeholder="Enter Last Name"
                    required />
                <br /><br />

                <input type="email" value={email}
                    onChange={onChangeEmail} placeholder="Enter Email"
                    required />
                <br /><br />

                <input type="number" value={emobile}
                    onChange={onChangeEmpMobile} placeholder="Enter Mobile No"
                    required />
                <br /><br />

                <input type="date" value={edob}
                    onChange={onChangeEmpDOB} />
                <br /><br />

                <input type="password" value={pass}
                    onChange={onChangePass} placeholder="Enter Password"
                    required />
                <br /><br />

                <input type="radio" name="gender" value="MALE"
                    checked={egender === 'MALE'}
                    onChange={onChangeEmpGender} />
                <label>Male</label>

                <input type="radio" name="gender" value="FEMALE"
                    checked={egender === 'FEMALE'}
                    onChange={onChangeEmpGender} />
                <label>Female</label>
                <br /><br />

                <select value={ecountry} onChange={onChangeEmpCountry}>
                    <option value="AF">Afghanistan</option>
                    <option value="India">India</option>
                    <option value="UK">UK</option>
                    <option value="USA">USA</option>
                </select>
                <br /><br />

                <label>ADDRESS: </label> <br />
                <textarea value={eaddress}
                    onChange={onChangeEmpAddress} rows="3" >
                </textarea>
                <br /><br />

                <input type="submit" value="REGISTER" className="btn btn-primary" />

            </form>
        </div>
    )
}


export default Registration
